/* Keeps an event listener on resize, provides context to children with a ResponsiveLayout */
import React, {createContext, useContext, useState, useEffect} from 'react';

const DimensionsContext = createContext();

/* dimensions object */
const windowDims = () => ({
  height: window.innerHeight,
  width: window.innerWidth,
});

const DimensionsProvider = ({children}) => {
  /* Dimensions state to be kept updated */
  const [dimensions, setDimensions] = useState(windowDims);
  useEffect(() => {
    const handleResize = () => {
      setDimensions(windowDims);
    };
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);
  return (
    <DimensionsContext.Provider value={dimensions}>
      {children}
    </DimensionsContext.Provider>
  );
};

export default DimensionsProvider;

export const useDimensions = () => {
  return useContext(DimensionsContext);
};
